import React, { useState, useEffect } from "react";
import SearchForm from "./components/SearchForm";
import SearchResults from "./components/SearchResults";

import { useSelector, useDispatch } from "react-redux";
import { addQuery } from "./actions";

import "./App.css";

const numResults = 5;
const API = "http://hn.algolia.com/api/v1/search";
const FRONT_PAGE_STORIES = `${API}?tags=front_page&hitsPerPage=${numResults}`;
const LATEST_STORIES = `${API}_by_date?tags=story&hitsPerPage=${numResults}`;

const App = () => {
  const queries = useSelector(redux => redux.queries);
  const dispatch = useDispatch();

  const [stories, setStories] = useState({});

  const fetchStories = async (request, title) => {
    const res = await fetch(request);
    res.json().then(res => setStories(res, title));
  };

  const search = query => {
    fetchStories(
      `${API}?query=${query}&tags=story&hitsPerPage=${numResults}&page=1`
    );
    dispatch(addQuery(query));
  };

  useEffect(() => {
    fetchStories(FRONT_PAGE_STORIES);
  }, []);

  return (
    <div>
      <button onClick={() => fetchStories(FRONT_PAGE_STORIES)}>
        Top Stories
      </button>
      <button onClick={() => fetchStories(LATEST_STORIES)}>
        Latest Stories
      </button>
      <SearchForm search={search} />
      <hr />
      {stories.hits ? (
        <SearchResults stories={stories} queries={queries} />
      ) : (
        "Loading"
      )}
    </div>
  );
};

export default App;
