import React from "react";
import { useSelector, useDispatch } from "react-redux";
import { removeQuery } from "../actions";

const SearchHistory = ({ queries, search }) => {
  const dispatch = useDispatch();

  return (
    <div>
      <hr />
      <h2>Redux Search History</h2>
      {queries.length > 0 ? (
        queries.map((query, index) => (
          <p key={index}>
            {query} <button onClick={search(query)}>View Results</button>{" "}
            <button onClick={dispatch(removeQuery(query))}>Remove</button>
          </p>
        ))
      ) : (
        <p>No searches yet...</p>
      )}
    </div>
  );
};

export default SearchHistory;
